mutation GeneratePaymentMethodTokenizationClientToken($input: GeneratePaymentMethodTokenizationClientTokenInput!) {
  generatePaymentMethodTokenizationClientToken(input: $input) {
    ... on ClientToken {
      value
      expirationDate
      usage
    }
    ... on UserError {
      errors {
        path
        code
        description
        errorPath
      }
    }
  }
}
