query GetAllAPIKeys($first: Int, $after: String) {
  organizations {
    apiKeys(first: $first, after: $after) {
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          name
          redactedKey
          createdAt
        }
      }
    }
  }
}

mutation CreateSecretAPIKey($input: CreateSecretAPIKeyInput!) {
  createSecretAPIKey(input: $input) {
    __typename  
    ... on APIKey {
      id
      name
      key
    }
  }
}

mutation RevokeAPIKey($input: RevokeAPIKeyInput!) {
  revokeAPIKey(input: $input) {
    ...on APIKey {
      id
    }
  }
}

query GetAllOrganizations {
  organizations {
    id
    profile {
      displayName
      environment
      region
    }
    accounts {
      edges {
        node {
          createdAt
          updatedAt
          __typename
          id
          name
          ledgers {
            creditBalance {
              value
            }
            debitBalance {
              value
            }
          }
        }
      }
    }
    cardProducts(first: 20) {
      pageInfo {
        startCursor
        endCursor
        hasNextPage
        hasPreviousPage
      }
      edges {
        cursor
        node {
          __typename
          id
          name
          usage
          vertical
          accounts {
            edges {
              node {
                id
                name
              }
            }
          }
        }
      }
    }
  }
}

query GetOrganizationByID($id: ID!) {
  node(id: $id) {
    ... on Organization {
      id
      profile {
        displayName
        environment
        region
      }
      financialAccounts {
        ledgers {
          id
          name
        }
      }
    }
  }
}

mutation UpdateOrganizationProfileDisplayName($input: UpdateOrganizationProfileDisplayNameInput!) {
  updateOrganizationProfileDisplayName(input: $input) {
    ... on Organization {
