        displayName
      }
    }
  }
}

mutation CreateCardProduct($input: CreateCardProductInput!) {
  createCardProduct(input: $input) {
    __typename
    ... on CardProduct {
      id
      name
      usage
      vertical
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
  }
}

mutation EnableCreditCardFeature(
	$input: EnableCreditCardFeatureInput!
) {
	enableCreditCardFeature(input: $input) {
		__typename			
		... on CardProduct {
			id
			features {
				__typename
				enabled
			}
		}
		... on UserError {
			errors {
				path
				code
				description
			}
		}
	}
}

mutation EnableCollaborativeApplicationUnderwritingFeature($input: EnableCollaborativeApplicationUnderwritingFeatureInput!) {
  enableCollaborativeApplicationUnderwritingFeature(input: $input) {
    ... on CardProduct {
      id
      name
      features {
        ... on CollaborativeApplicationUnderwritingCardProductFeature {
          enabled
          createdAt
          updatedAt
        }
      }
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
    ... on AccessDeniedError {
      message
    }
  }
}

query GetCardProductWithCreditConfigs($id: ID!) {
  node(id: $id) {
    ... on CardProduct {
      __typename
      id
      creditConfiguration {
        accountAging {
          delinquentInDays
          suspendedInDays
          chargeOffInDays
          lateFeeGracePeriodInDays
        }
        billingCycleConfiguration {
          billingCycleGracePeriodInDays
          billingCycleGracePeriodType
          billingCyclePeriod
        }
        interest {
          includesFees
          includesPurchase
        }
        minimumPayment {
          includesInterestAmount
          includesFeesCharged
          includesPastDueAmount
          percentage
          thresholdAmount {
            currencyCode
            value
          }
        }
      }
    }
  }
}

query Ping {
  ping
}

query IntrospectionQuery {
  __schema {
    queryType {
      name
    }
    mutationType {
      name
    }
    subscriptionType {
      name
    }
    types {
      ...FullType
    }
    directives {
      name
      description

      locations
      args {
        ...InputValue
      }
    }
  }
}

fragment FullType on __Type {
  kind
  name
  description
  fields(includeDeprecated: true) {
    name
    description
    args {
      ...InputValue
    }
    type {
      ...TypeRef
    }
    isDeprecated
    deprecationReason
  }
  inputFields {
    ...InputValue
  }
  interfaces {
    ...TypeRef
  }
  enumValues(includeDeprecated: true) {
    name
    description
    isDeprecated
    deprecationReason
  }
  possibleTypes {
    ...TypeRef
  }
}

fragment InputValue on __InputValue {
  name
  description
  type {
    ...TypeRef
  }
  defaultValue
}

fragment TypeRef on __Type {
  kind
  name
  ofType {
    kind
    name
    ofType {
      kind
      name
      ofType {
        kind
        name
        ofType {
          kind
          name
          ofType {
            kind
            name
            ofType {
              kind
              name
              ofType {
                kind
                name
              }
            }
          }
        }
      }
    }
  }
}

