fragment accountHolder on USPersonAccountHolder {
  id
  email
  dateOfBirth
  externalId
  createdAt
  updatedAt
  name {
    givenName
    familyName
    middleName
    suffix
    title
  }
  externalFinancialAccounts {
    edges {
      node {
        __typename
        ... on ExternalFinancialBankAccount {
          id
        }
      }
    }
  }
  identificationDocument {
    socialSecurityNumber {
      numberHash
      countryCodeAlpha3
    }
  }
  billingAddress {
    streetAddress
    extendedAddress
    postalCode
    region
    locality
    countryCodeAlpha3
  }
  phoneNumbers {
    number
    countryCode
    label
  }
  financialAccounts {
    edges {
      node {
        name
        owner {
          __typename
        }
      }
    }
  }
  paymentCards {
    pageInfo {
      hasNextPage
      hasPreviousPage
      startCursor
      endCursor
    }
    edges {
      node {
        id
        last4
        formFactor
        financialAccounts {
          __typename
          id
          name
        }
      }
    }
  }
  cardProductApplications {
    pageInfo {
      hasNextPage
      hasPreviousPage
      startCursor
      endCursor
    }
    edges {
      node {
        id
        applicationState {
          status
        }
      }
    }
  }
}

query ListAccountHolders($first: Int, $filterBy: PersonAccountHolderFilterInput) {
  personAccountHolders(first: $first, filterBy: $filterBy) {
