query GetAccountHolderCardProductApplicationStatus(
  $id: ID!
  $firstApplicationHistory: Int
) {
  node(id: $id) {
    ... on AccountHolderCardProductApplication {
      __typename
      id
      createdAt
      updatedAt
      applicationState {
        status
      }
      cardProduct {
        name
      }
      accountHolderSnapshot {
        ...USPersonAccountHolderSnapshot
      }
      applicationHistory(first: $firstApplicationHistory) {
        pageInfo {
          hasNextPage
          hasPreviousPage
          startCursor
          endCursor
        }
        edges {
          node {
            ...ApplicationHistorySnapshot
          }
        }
      }
    }
  }
}

fragment Verification on AccountHolderVerification {
  status
  reason
  results {
      code
      description
    }
  requiredDocuments {
    __typename
    createdAt
    referenceIdentifier
    updatedAt
    status
    documentUploadSession {
      ... on USAccountHolderApplicationDocumentUploadSession {
        status
        id
        primaryDocumentTypes
        secondaryDocumentTypes
        uploadRequirements {
          constraints {
            ... on DocumentUploadRequirementConstraint {
              numberOfDocuments
              documentType
            }
          }
        }
      }
    }
    uploadedDocuments {
      ... on AccountHolderApplicationDocument {
        status
        type
        createdAt
        updatedAt
      }
    }
  }
}

fragment USPersonAccountHolderSnapshot on USPersonAccountHolderSnapshot {
  __typename
  accountHolderCurrent {
    id
  }
  name {
    givenName
    familyName
    title
    suffix
  }
  currentVerification {
    ...Verification
  }
}


fragment ApplicationHistorySnapshot on AccountHolderCardProductApplicationSnapshot {
