mutation IssuePaymentCardForFinancialAccount($input: IssuePaymentCardForFinancialAccountInput!) {
  issuePaymentCardForFinancialAccount(input: $input) {
    ... on PaymentCard {
      id
      bin
      last4
      expirationDate
      network
      status
      formFactor
      financialAccounts {
        ledgers {
          normalBalance
          creditBalance {
            value
          }
          debitBalance {
            value
          }
        }
      }
      cardProductApplication {
				... on AccountHolderCardProductApplication {
					id
					applicationState {
						status
					}
					accountHolderSnapshot {
						__typename
						... on USPersonAccountHolderSnapshot {
							name {
								givenName
								familyName
							}

							billingAddress {
								streetAddress
								locality
								postalCode
								countryCodeAlpha3
							}

							currentVerification {
								status
								reason
								results {
									code
									description
								}
							}
						}
					}
        }
      }

      restrictedDetails {
        ... on PaymentCardRestrictedDetails {
          cvv
          number
        }
        ... on AccessDeniedError {
          message
        }
      }
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
  }
}

