query GetFinancialAccountActivities(
  $id: ID!
  $first: Int
  $after: String
  $filterBy: FinancialAccountActivitiesFilterInput
) {
  node(id: $id) {
    __typename
    ... on FinancialAccount {
      id
      financialAccountActivities(
        first: $first
        after: $after
        filterBy: $filterBy
      ) {
        pageInfo {
          hasNextPage
          startCursor
          endCursor
        }
        edges {
          cursor
          node {
            __typename
            sign
            isComplete
            pendingAmount {
              ...AmountFragment
            }
            postedAmount {
              ...AmountFragment
            }
            createdAt
            source {
              ...TransactionFragment
              ... on FeeTransferEvent {
                feeTransfer {
                  ... on ChargedFeeTransfer {
                    feeActivityType
                    status {
                      ... on NonFailureFeeTransferStatus {
                        status
                      }
                      ... on FailureFeeTransferStatus {
                        status
                        reasonDetails {
                          reason
                          type
                        }
                      }
                    }
                    transferDate
                  }
                }
              }
              ... on ElectronicFundsTransfer {
                ... on AchTransfer {
                  companyEntryDescription
                  companyIdentifier
                  companyName
                  failedAt
                  individualIdentificationNumber
                  individualName
                  paymentRelatedInformation
                  purpose
                  sign
                  statusFailureReason
                  traceNumber
                  type
                }
              }
              ... on InterFinancialAccountTransfer {
                memo
                purpose
                status
                statusReason
                transferActivityType
              }
            }
          }
        }
      }
    }
  }
}

