query GetDocumentUploadSession($id: ID!) {
  node(id: $id) {
    ... on DocumentUploadSession {
      id
      createdAt
      updatedAt
      status
      documents {
        ... on DocumentUploadLink {
          id
          status
          uploadUrl
          documentType
          documentCategoryType
        }
      }
    }
    ... on AccountHolderCardProductApplication {
      accountHolderSnapshot {
        ... on USPersonAccountHolderSnapshot {
          currentVerification {
            requiredDocuments {
              documentUploadSession {
                ... on USAccountHolderApplicationDocumentUploadSession {
                  id
                  status
                  primaryDocumentTypes
                  secondaryDocumentTypes
                  supportingDocumentTypes
                  uploadRequirements {
                    constraints {
                      ... on DocumentUploadRequirementConstraint {
                        documentType
                        numberOfDocuments
                      }
                    }
                  }
                }
              }
            }
          }
        }
        ... on USBusinessAccountHolderSnapshot {
          currentVerification {
            requiredDocuments {
              documentUploadSession {
                ... on USAccountHolderApplicationDocumentUploadSession {
                  id
                  status
                  primaryDocumentTypes
                  secondaryDocumentTypes
                  supportingDocumentTypes
                  uploadRequirements {
                    constraints {
                      ... on DocumentUploadRequirementConstraint {
                        documentType
                        numberOfDocuments
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}

