query FindFinancialAccountLedgerActivity($id: ID!, $first: Int, $filterBy: LedgerEntriesFilterInput, $after: String) {
  node(id: $id) {
    __typename
    ... on FinancialAccount {
      __typename
      id
      ledgers(ledgerNames: [OUTSTANDING_BALANCE_PAYABLE]) {
        id
        normalBalance
        asOf
        name
        creditBalance {
          value
          currencyCode
          decimalPlaces
          __typename
        }
        debitBalance {
          value
          currencyCode
          decimalPlaces
          __typename
        }
        ledgerEntries(first: $first, filterBy: $filterBy, after: $after) {
          ... on LedgerEntryConnection {
            pageInfo {
              startCursor
              endCursor
              hasNextPage
              hasPreviousPage
              __typename
            }
            edges {
              cursor
              __typename
              node {
                ...LedgerEntryNodeSelection
                __typename
              }
            }
            __typename
          }
          __typename
        }
        __typename
      }
    }
  }
}

