mutation SimulateSingleStepAuthAndClear($input: SimulateSingleStepAuthAndClearInput!) {
  simulateSingleStepAuthAndClear(input: $input) {
    ... on AuthorizationAndClearEvent {
      id
      responseCode
      transaction {
        ... on DebitTransaction {
          id
        }
        ... on CreditTransaction {
          id
        }
      }
    }
  }
}

mutation simulateCreateAndActivateFeeSchedule(
  $input: SimulateCreateAndActivateFeeScheduleInput!
) {
  simulateCreateAndActivateFeeSchedule(input: $input) {
    __typename
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
    ... on FeeSchedule {
      __typename
      id
      feeScheduleAssociatedTo {
        __typename
        ... on CardProduct {
          id
        }
      }
      feeConfigurations {
        id
        feeActivityType
        feeChargeRules {
          id
          name
          order
          value {
            __typename
            ... on FixedFeeChargeValue {
              amount {
                value
                currencyCode
              }
            }
            ... on VariableFeeChargeValue {
              percentage
            }
          }
          conditions {
            __typename
            ... on FeeChargeRuleAmountCondition {
              id
              description
              condition {
                __typename
                ... on AmountFeeCondition {
                  value {
                    __typename
                    ... on AmountFeeConditionValue {
                      amount {
                        value
                        currencyCode
                      }
                      maximumAmount {
                        value
                        currencyCode
                      }
                    }
                  }
                  operator
                }
              }
              aggregatedAt
              aggregationDuration
            }
            ... on FeeChargeRuleCountCondition {
              id
              description
              condition {
                __typename
                ... on CountFeeCondition {
                  value {
                    __typename
                    ... on CountFeeConditionValue {
                      count
                    }
                  }
                  operator
                }
              }
              aggregatedAt
              aggregationDuration
            }
            ... on FeeChargeRuleAmountCondition {
              id
              description
              condition {
                __typename
                ... on AmountFeeCondition {
                  value {
                    __typename
                    ... on AmountFeeConditionValue {
                      amount {
                        value
                        currencyCode
                      }
                    }
                  }
                  operator
                }
              }
              aggregatedAt
              aggregationDuration
            }
          }
        }
      }
      activationDate
      status
      createdAt
      updatedAt
    }
  }
}

