query FindFinancialAccount($id: ID!) {
  node(id: $id) {
    __typename
    ... on FinancialAccount {
      id
      feeTransferEvents(first: 10) {
        __typename
        pageInfo {
          hasNextPage
          hasPreviousPage
          startCursor
          endCursor
        }
        edges {
          cursor
          node {
            __typename
            id
            feeTransfer {
              __typename
              ... on ChargedFeeTransfer {
                originatingAccount {
                  id
                }
                receivingAccount {
                  id
                }
                cardProduct {
                  id
                }
                feeActivityType
                feeAmount {
                  value
                  currencyCode
                }
                transferedFeeAmount {
                  value
                  currencyCode
                }
                transferDate
                status {
                  ... on NonFailureFeeTransferStatus {
                    status
                  }
                  ... on FailureFeeTransferStatus {
                    status
                    reasonDetails {
                      type
                      reason
                    }
                  }
                }
                feeChargeRule {
                  __typename
                  id
                  name
                  value {
                    __typename
                    ... on FixedFeeChargeValue {
                      amount {
                        value
                        currencyCode
                      }
                    }
                    ... on VariableFeeChargeValue {
                      percentage
                    }
                  }
                  conditions {
                    __typename
                    ... on FeeChargeRuleAmountCondition {
                      id
                      description
                      condition {
                        __typename
                        ... on AmountFeeCondition {
                          value {
                            __typename
                            ... on AmountFeeConditionValue {
                              amount {
                                value
                                currencyCode
                              }
                              maximumAmount {
                                value
                                currencyCode
                              }
                            }
                          }
                          operator
                        }
                      }
                      aggregatedAt
                      aggregationDuration
                      createdAt
                      updatedAt
                    }
                    ... on FeeChargeRuleCountCondition {
                      id
                      description
                      condition {
                        __typename
                        ... on CountFeeCondition {
                          value {
                            __typename
                            ... on CountFeeConditionValue {
                              count
                              maximumCount
                            }
                          }
                          operator
                        }
                      }
                      aggregatedAt
                      aggregationDuration
                      createdAt
                      updatedAt
                    }
                  }
                  createdAt
                  updatedAt
                }
                feeChargeActvity {
                  __typename
                  ... on PhysicalPaymentCardOrder {
                    id
                  }
                  ... on PaymentCard {
                    id
                  }
                  ... on FinancialAccount {
                    id
                  }
                  ... on SecureDepositACHTransfer {
                    id
                  }
                  ... on SecureCardBalanceRepaymentACHTransfer {
                    id
                  }
                  ... on IntegratorInitiatedFundsWithdrawalACHTransfer {
                    id
                  }
                  ... on IntegratorInitiatedFundsDepositACHTransfer {
                    id
                  }
                  ... on AuthorizationAndClearEvent {
                    id
                  }
                  ... on ClearingEvent {
                    id
                  }
                  ... on BalanceInquiryEvent {
                    id
                  }
                }
              }
              ... on ReversedFeeTransfer {
                originatingAccount {
                  id
                }
                receivingAccount {
                  id
                }
                transferedFeeAmount {
                  value
                  currencyCode
                }
                transferDate
                status {
                  ... on NonFailureFeeTransferStatus {
                    status
                  }
                  ... on FailureFeeTransferStatus {
                    status
                    reasonDetails {
                      type
                      reason
                    }
                  }
                }
                sourceFeeTransferEvent {
                  id
                }
              }
            }
          }
        }
      }
    }
  }
}

query GetCardProductWithLedgers(
  $cardProductId: ID!
  $firstLedgerEntries: Int
  $ledgerEntriesAfterCursor: String
  $filterBy: AccountHolderFinancialAccountsFilterInput
) {
  node(id: $cardProductId) {
    __typename
    ... on CardProduct {
      id
      name
      accounts(first: 20, filterBy: $filterBy) {
        __typename
        pageInfo {
          __typename
          endCursor
          hasNextPage
          startCursor
          hasPreviousPage
        }
        edges {
          __typename
          cursor
          node {
             __typename
            id
            name
            createdAt
            updatedAt
            features {
              __typename
              enabled
              createdAt
              updatedAt
            }
            ledgers {
              __typename
              id
              normalBalance
              asOf
              name
              creditBalance {
                value
                currencyCode
                __typename
              }
              debitBalance {
                value
                currencyCode
                __typename
              }
              ledgerEntries(
                first: $firstLedgerEntries
                after: $ledgerEntriesAfterCursor
              ) {
                __typename
                pageInfo {
                  hasNextPage
                  hasPreviousPage
                  startCursor
                  endCursor
                  __typename
                }
                edges {
                  __typename
                  cursor
                  node {
                    id
                    __typename
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}

query ListFeeTransferEvents(
  $first: Int
  $after: String
  $filterBy: FeeTransferEventFilterInput
) {
  feeTransferEvents(first: $first, after: $after, filterBy: $filterBy) {
    pageInfo {
      startCursor
      endCursor
      hasNextPage
      hasPreviousPage
    }
    edges {
      cursor
      node {
        ...feeTransferEvent
      }
    }
  }
}
fragment feeTransferEvent on FeeTransferEvent {
  __typename
  id
  feeTransfer {
    __typename
    ... on ChargedFeeTransfer {
      originatingAccount {
        id
        accountStatus
        feeTransferEvents {
          pageInfo {
            hasNextPage
          }
          edges {
            cursor
            node {
              id
              feeTransfer {
                __typename
              }
            }
          }
        }
      }
      receivingAccount {
        id
        accountStatus
      }
      cardProduct {
        id
        name
      }
      feeActivityType
      feeAmount {
        value
        currencyCode
      }
      transferedFeeAmount {
        value
        currencyCode
      }
      transferDate
      status {
        __typename
        ... on NonFailureFeeTransferStatus {
          status
        }
      }
      feeChargeRule {
        id
        name
        value {
          __typename
          ... on FixedFeeChargeValue {
            amount {
              value
              currencyCode
            }
          }
        }
      }
      feeChargeActvity {
        __typename
        ... on PhysicalPaymentCardOrder {
          id
        }
        ... on PaymentCard {
          id
        }
        ... on FinancialAccount {
          id
        }
        ... on SecureDepositACHTransfer {
          id
        }
        ... on SecureCardBalanceRepaymentACHTransfer {
          id
        }
        ... on IntegratorInitiatedFundsWithdrawalACHTransfer {
          id
        }
        ... on IntegratorInitiatedFundsDepositACHTransfer {
          id
        }
        ... on AuthorizationAndClearEvent {
          id
          transaction {
            __typename
            ... on CreditTransaction {
              id
            }
            ... on DebitTransaction {
              id
            }
          }
        }
        ... on ClearingEvent {
          id
          transaction {
            __typename
            ... on CreditTransaction {
              id
            }
            ... on DebitTransaction {
              id
            }
          }
        }
        ... on BalanceInquiryEvent {
          id
          transaction {
            __typename
            ... on CreditTransaction {
              id
            }
            ... on DebitTransaction {
              id
            }
          }
        }
      }
    }
    ... on ReversedFeeTransfer {
      originatingAccount {
        id
      }
      receivingAccount {
        id
      }
      transferedFeeAmount {
        value
        currencyCode
      }
      transferDate
      sourceFeeTransferEvent {
        __typename
        id
        ... on FeeTransferEvent {
          id
          feeTransfer {
            __typename
            ... on ChargedFeeTransfer {
              originatingAccount {
                id
              }
              feeAmount {
                value
                currencyCode
              }
            }
          }
        }
      }
    }
  }
}

query GetFinancialAccountWithLedgerEntriesByID($id: ID!, $ledgerNames: [LedgerName!]) {
  node(id: $id) {
    ... on FinancialAccount {
      __typename
      id
      name
      owner {
        __typename
      }
      cardProduct {
        id
      }
      ledgers(ledgerNames: $ledgerNames) {
        id
        name
        normalBalance
        creditBalance {
          value
        }
        debitBalance {
          value
        }
        ledgerEntries {
          edges {
            node {
              __typename
              id
              createdAt
              amount {
                value
                currencyCode
              }
              financialEvent {
                ... on WireTransfer {
                  __typename
                  id
                  amount {
                    value
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}
