operations.graphql:
mutation CreateExternalPhysicalPaymentCardOrder($input: CreateExternalPhysicalPaymentCardOrderInput!) {
    createExternalPhysicalPaymentCardOrder(input: $input) {
        ... on ExternalPhysicalPaymentCardOrder {
            id
            createdAt
            updatedAt
            card {
                id
            }
            embeddedDevice {
                formFactor
                image
                name
                serialNumber
                variant
            }
            status
        }
        ... on UserError {
            errors {
                errorPath
                code
                description
            }
        }
    }
}

query GetExternalPhysicalPaymentCardOrder($id: ID!) {
    node(id: $id) {
        ... on ExternalPhysicalPaymentCardOrder {
            id
            createdAt
            updatedAt
            card {
                id
            }
            embeddedDevice {
                formFactor
                image
                name
                serialNumber
                variant
            }
            status
        }
    }
}

query GetPaymentCardExternalPhysicalPaymentCardOrders($id: ID!) {
    node(id: $id) {
        ... on PaymentCard {
            externalPhysicalPaymentCardOrders {
                id
                createdAt
                updatedAt
                card {
                    id
                }
                embeddedDevice {
                    formFactor
                    image
                    name
                    serialNumber
                    variant
                }
                status
            }
        }
    }
}
