query GetAllAPIKeys($first: Int, $after: String) {
  organizations {
    apiKeys(first: $first, after: $after) {
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          name
          redactedKey
          createdAt
        }
      }
    }
  }
}

