fragment USPersonAccountHolderSnapshot on USPersonAccountHolderSnapshot {
  __typename
  currentVerification {
    status
    reason
    results {
      code
      description
    }
    requiredDocuments {

      createdAt
      referenceIdentifier
      updatedAt
      status
          documentUploadSession {
            ... on USAccountHolderApplicationDocumentUploadSession {
              status
              id
              primaryDocumentTypes
              recommendedDocumentTypes
            }
          }
      uploadedDocuments {
        ... on AccountHolderApplicationDocument {
          status
          type
          createdAt
          updatedAt
        }
      }
    }
  }
}

fragment USBusinessAccountHolderSnapshot on USBusinessAccountHolderSnapshot {
  __typename
  primaryAuthorizedPerson {
    currentVerification {
      status
      reason
      requiredDocuments {
        createdAt
        referenceIdentifier
        updatedAt
        status
          documentUploadSession {
            ... on USAccountHolderApplicationDocumentUploadSession {
              status
              id
              primaryDocumentTypes
              recommendedDocumentTypes
            }
          }
        uploadedDocuments {
          ... on AccountHolderApplicationDocument {
            status
            type
            createdAt
            updatedAt
          }
        }
      }
    }
  }
  businessProfile {
    currentVerification {
      status
      reason
      requiredDocuments {
        createdAt
        referenceIdentifier
        updatedAt
        status
          documentUploadSession {
            ... on USAccountHolderApplicationDocumentUploadSession {
              status
              id
              primaryDocumentTypes
              recommendedDocumentTypes
            }
          }
        uploadedDocuments {
          ... on AccountHolderApplicationDocument {
            status
            type
            createdAt
            updatedAt
          }
        }
      }
    }
    ultimateBeneficialOwners {
      currentVerification {
        status
        reason
        requiredDocuments {
          createdAt
          referenceIdentifier
          updatedAt
          status
          documentUploadSession {
            ... on USAccountHolderApplicationDocumentUploadSession {
              status
              id
              primaryDocumentTypes
              recommendedDocumentTypes
            }
          }
          uploadedDocuments {
            ... on AccountHolderApplicationDocument {
              status
              type
              createdAt
              updatedAt
            }
          }
        }
      }
    }
  }
}

