fragment AmountFragment on Amount {
  value
  decimalPlaces
  currencyCode
}

fragment MerchantDetailsFragment on MerchantDetails {
  name
  description
  category
  categoryCode
  merchantId
  address {
    streetAddress
    locality
    region
    postalCode
  }
}

fragment TransactionEventFragment on TransactionEvent {
  responseCode
  currentFinancialAccountAvailableToSpendAmount {
    ...AmountFragment
  }
  ... on AuthorizationEvent {
    merchantDetails {
      ...MerchantDetailsFragment
    }
  }
  ... on ClearingEvent {
    merchantDetails {
      ...MerchantDetailsFragment
    }
  }
  ... on AuthorizationAndClearEvent {
    merchantDetails {
      ...MerchantDetailsFragment
    }
  }
