mutation ValidateAddress($input: ValidateAddressInput!) {
    validateAddress(input: $input) {
        ... on AddressValidationResult {
            outcome {
                ... on AddressValidationOutcome {
                    ... on AddressValidatedResult {
                        token {
                            ... AddressValidatedResultTokenFragment
                        }
                    }
                }
                ... on AddressValidatedWithChangesResult {
                    componentsChanged
                    token {
                        ... AddressValidatedResultTokenFragment
                    }
                }
                ... on AddressIncompleteResult {
                    componentsMissing
                    provided {
                        ... AddressFragment
                    }
                }
                ... on AddressInvalidResult {
                    componentsInvalid
                    provided {
                        ... AddressFragment
                    }
                }
            }
        }
        ... on UserError {
            errors {
                code
                description
                errorPath
            }
        }
    }
}

