query searchTransactions($first: Int, $after: String, $filterBy: TransactionSearchFilterInput!) {
    organizations {
        searchTransactions(first: $first, after: $after, filterBy: $filterBy) {
            ... on TransactionSearchResultConnection {
                pageInfo {
                    hasNextPage
                    hasPreviousPage
                    startCursor
                    endCursor
                }
                edges {
                    cursor
                    node {
                        ... on TransactionSearchResult {
                            source {
                                __typename
                            }
                            amount {
                                currencyCode
                                value
                            }
                            status
                            transactionSearchType
                            transactionSearchObjectType
                        }
                    }
                }
            }
            ... on AccessDeniedError {
                message
            }
            ... on UserError {
                errors {
                    errorPath
                    code
                    description
                }
            }
        }
    }
}
