}

mutation CreateCardProduct($input: CreateCardProductInput!) {
  createCardProduct(input: $input) {
    __typename
    ... on CardProduct {
      id
      name
      usage
      vertical
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
  }
}

query GetCardProduct($id: ID!) {
    node(id: $id) {
        ... on CardProduct {
            id
            name
            accounts {
                edges {
                    node {
                        __typename
                        id
                        name
                        features {
                            __typename
                        }
                    }
                }
            }
        }
    }
}

query Ping {
  ping
}

query IntrospectionQuery {
  __schema {
    queryType {
      name
    }
    mutationType {
      name
    }
    subscriptionType {
      name
    }
    types {
      ...FullType
    }
    directives {
      name
      description

      locations
      args {
        ...InputValue
      }
    }
  }
}

fragment FullType on __Type {
  kind
  name
  description
  fields(includeDeprecated: true) {
    name
    description
    args {
      ...InputValue
    }
    type {
      ...TypeRef
    }
    isDeprecated
    deprecationReason
  }
  inputFields {
    ...InputValue
  }
  interfaces {
    ...TypeRef
  }
  enumValues(includeDeprecated: true) {
    name
    description
    isDeprecated
    deprecationReason
  }
  possibleTypes {
    ...TypeRef
  }
}

fragment InputValue on __InputValue {
  name
  description
  type {
    ...TypeRef
  }
  defaultValue
}

fragment TypeRef on __Type {
  kind
  name
  ofType {
    kind
    name
    ofType {
      kind
      name
      ofType {
        kind
        name
        ofType {
          kind
          name
          ofType {
            kind
            name
            ofType {
              kind
              name
              ofType {
                kind
                name
              }
            }
          }
        }
      }
    }
  }
}

mutation CreateUSPersonAuthorizedUser(
  $input: CreateUSPersonAuthorizedUserInput!
) {
  createUSPersonAuthorizedUser(input: $input) {
    ... on USPersonAuthorizedUser {
      id
      name {
        givenName
        familyName
      }
      email
      phoneNumbers {
        label
        countryCode
        number
      }
      billingAddress {
        streetAddress
        extendedAddress
        locality
        region
        postalCode
        countryCodeAlpha3
      }
      dateOfBirth
      externalId
      associatedAccountHolders {
        ... on AccountHolderConnection {
          edges {
            node {
              ... on USBusinessAccountHolder {
                id
              }
            }
          }
        }
      }
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
  }
}

