          id
          createdAt
          updatedAt
          website
          name {
            legalBusinessName
            doingBusinessAsName
          }
          billingAddress {
            streetAddress
            postalCode
            region
            locality
          }
          identificationDocument {
            employerIdentificationNumber {
              numberHash
            }
          }
          phoneNumbers {
            countryCode
            number
            label
          }
          ultimateBeneficialOwners {
            id
            createdAt
            updatedAt
            name {
              givenName
              familyName
            }
            dateOfBirth
            email
            homeAddress {
              streetAddress
              postalCode
              region
              locality
            }
            identificationDocument {
              socialSecurityNumber {
                numberHash
              }
            }
            percentageOwnership
            phoneNumbers {
              countryCode
              number
              label
            }
          }
        }
        primaryAuthorizedPerson {
          id
          createdAt
          updatedAt
          authorizingPersonTitle
          name {
            givenName
            familyName
          }
          dateOfBirth
          email
          percentageOwnership
          homeAddress {
            streetAddress
            postalCode
            region
            locality
          }
          identificationDocument {
            socialSecurityNumber {
              numberHash
            }
          }
          phoneNumbers {
            countryCode
            number
            label
          }
        }
        cardProductApplications {
          edges {
            node {
              id
              applicationState {
                status
              }
            }
          }
        }
        financialAccounts {
          edges {
            node {
              id
              name
              owner {
                __typename
