query GetUSBusinessAccountHolderByID($id: ID!) {
  node(id: $id) {
    ... on USBusinessAccountHolder {
      id
      externalId
      updatedAt
      createdAt
      externalFinancialAccounts {
        edges {
          node {
            ... on ExternalFinancialBankAccount {
              id
              externalBankAccountDetails {
                id
                last4
                type
                routingNumber
              }
            }
          }
        }
      }
      financialAccounts {
        edges {
          node {
            id
            externallyInitiatedAchTransfers {
              edges {
                node {
                  __typename
                  ... on SecureDepositACHTransfer {
                    id
                    amount {
                      value
                      currencyCode
                    }
                    status {
                      __typename
                      ... on NonFailureIntegratorInitiatedACHStatus {
                        status
                      }
                      ... on FailureIntegratorInitiatedACHStatus {
                        status
                        statusReasonCode
                      }
                    }
                    settlementDate
                  }
                }
              }
            }
            integratorInitiatedAchTransfers {
              edges {
                node {
                  __typename
                  ... on SecureDepositACHTransfer {
                    id
                    amount {
                      value
                      currencyCode
                    }
                    status {
                      __typename
                      ... on NonFailureIntegratorInitiatedACHStatus {
                        status
                      }
                      ... on FailureIntegratorInitiatedACHStatus {
                        status
                        statusReasonCode
                      }
                    }
                    settlementDate
                  }
                }
              }
            }
            incomingScheduledTransfers {
              edges {
                node {
                  id
                  ... on OneTimeACHTransfer {
                    scheduledTransferDate
                    transferEvents {
                      edges {
                        node {
                          id
                        }
                      }
                    }
                  }
                  ... on RecurringACHTransfer {
                    nextScheduledTransferDate
                    transferAmount {
                      __typename
                      ... on TransferAmountCalculation {
                        calculatedType
                      }
                      ... on ManualTransferAmount {
                        amount {
                          value
                          currencyCode
                        }
                      }
                    }
                  }
                }
              }
            }
            ledgers {
              id
              name
              normalBalance
              creditBalance {
                value
                currencyCode
              }
              debitBalance {
                value
                currencyCode
              }
              ledgerEntries {
                pageInfo {
                  startCursor
                  endCursor
                  hasNextPage
                  hasPreviousPage
                }
                edges {
                  cursor
                  node {
                    __typename
                    id
                    amount {
                      value
                    }
                    financialEvent {
                      __typename
                      ... on Transfer {
                        __typename
                        createdAt
                        updatedAt
                        id
                        status
                        statusReason
                        amount {
                          value
                          currencyCode
                        }
                      }
                      ... on TransactionEvent {
                        __typename
                        paymentCard {
                          id
                          last4
                        }
                        responseCode
                      }
                      ...AuthorizationEventFragment
                      ...ClearingEventFragment
                      ...ReversalEventFragment
                    }
                  }
                }
              }
            }
          }
        }
      }
      primaryAuthorizedPerson {
        id
        createdAt
        updatedAt
        authorizingPersonTitle
        name {
          givenName
          familyName
        }
        dateOfBirth
        email
        percentageOwnership
        homeAddress {
          streetAddress
          postalCode
          region
          locality
        }
        identificationDocument {
          socialSecurityNumber {
            numberHash
          }
        }
        phoneNumbers {
          countryCode
          number
          label
        }
      }
      businessProfile {
        name {
          legalBusinessName
          doingBusinessAsName
        }
        businessType
        phoneNumbers {
          number
        }
      }
      cardProductApplications {
        edges {
          node {
            id
            applicationState {
              status
            }
          }
        }
      }
    }
  }
}

query GetUSBusinessAccountHolderACHTransfers($id: ID!, $integratorInitiatedAchTransfersFilter: IntegratorInitiatedACHTransferFilterInput, $externallyInitiatedAchTransfersFilter: ExternallyInitiatedACHTransferFilterInput) {
  node(id: $id) {
    ... on USBusinessAccountHolder {
      financialAccounts {
        edges {
          node {
            id
            externallyInitiatedAchTransfers(filterBy: $externallyInitiatedAchTransfersFilter) {
              edges {
                node {
                  __typename
                  ... on SecureCardBalanceRepaymentACHTransfer {
                    id
                    amount {
                      value
                      currencyCode
                    }
                    status {
                      __typename
                      ... on NonFailureIntegratorInitiatedACHStatus {
                        status
                      }
                      ... on FailureIntegratorInitiatedACHStatus {
                        status
                        statusReasonCode
                      }
                    }
                    settlementDate
                  }
                  ... on SecureDepositACHTransfer {
                    id
                    amount {
                      value
                      currencyCode
                    }
                    status {
                      __typename
                      ... on NonFailureIntegratorInitiatedACHStatus {
                        status
                      }
                      ... on FailureIntegratorInitiatedACHStatus {
                        status
                        statusReasonCode
                      }
                    }
                    settlementDate
                  }
                }
              }
            }
            integratorInitiatedAchTransfers(filterBy: $integratorInitiatedAchTransfersFilter) {
              edges {
                node {
                  __typename
                  ... on SecureCardBalanceRepaymentACHTransfer {
                    id
                    amount {
                      value
                      currencyCode
                    }
                    status {
                      __typename
                      ... on NonFailureIntegratorInitiatedACHStatus {
                        status
                      }
                      ... on FailureIntegratorInitiatedACHStatus {
                        status
                        statusReasonCode
                      }
                    }
                    settlementDate
                  }
                  ... on SecureDepositACHTransfer {
                    id
                    amount {
                      value
                      currencyCode
                    }
                    status {
                      __typename
                      ... on NonFailureIntegratorInitiatedACHStatus {
                        status
                      }
                      ... on FailureIntegratorInitiatedACHStatus {
                        status
                        statusReasonCode
                      }
                    }
                    settlementDate
                  }
                }
              }
            }
            incomingScheduledTransfers {
              edges {
                node {
                  id
                  ... on OneTimeACHTransfer {
                    scheduledTransferDate
                    transferEvents {
                      edges {
                        node {
                          id
                        }
                      }
                    }
                  }
                  ... on RecurringACHTransfer {
                    nextScheduledTransferDate
                    transferAmount {
                      __typename
                      ... on TransferAmountCalculation {
                        calculatedType
                      }
                      ... on ManualTransferAmount {
                        amount {
                          value
                          currencyCode
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}

fragment ExternalFinancialBankAccountFragment on ExternalFinancialBankAccount {
  __typename
  id
  owner {
    __typename
  }
  name
  externalBankAccountDetails {
    id
    last4
