}

query GetUSBusinessAccountHolderScheduledTransfers($id: ID!) {
  node(id: $id) {
    ... on USBusinessAccountHolder {
      id
      financialAccounts {
        edges {
          node {
            id
            incomingScheduledTransfers {
              edges {
                node {
                  id
                  ... on ScheduledTransfer {
                    id
                    fromFinancialAccount {
                      ...ExternalFinancialBankAccountFragment
                    }
                    toFinancialAccount {
                      ...FinancialAccountFragment
                    }
                  }
                  ... on OneTimeACHTransfer {
                    id
                    fromFinancialAccount {
                      ...ExternalFinancialBankAccountFragment
                    }
                    toFinancialAccount {
                      ...FinancialAccountFragment
                    }
                    transferAmount {
                      __typename
                      ... on TransferAmountCalculation {
                        calculatedType
                      }
                      ... on ManualTransferAmount {
                        amount {
                          value
                          currencyCode
                        }
                      }
                    }
                    scheduledTransferDate
                    descriptor {
                      companyEntryDescription
                      individualName
                      individualIdentificationNumber
                    }
                    transferEvents {
                      edges {
                        node {
                          __typename
                          id
                          createdAt
                          updatedAt
                          ... on ScheduledTransferAchEvent {
                            paymentInitiatedAt
                            paymentPostedAt
                            status
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}

