query GetAccountHolderCardProductApplication($id: ID!) {
  node(id: $id) {
    ... on AccountHolderCardProductApplication {
      id
      applicationState {
        status
      }
      cardProduct {
        id
      }
      openOffers: offers(filterBy: {offerStatus: {includes: [OPEN]}}) {
        edges{
            node {
                ... on AccountHolderCardProductApplicationOffer {
                    id
                    name
                    category
                    status
                }
            }
        }        
      }
      rejectedOffers: offers(filterBy: {offerStatus: {includes: [REJECTED]}}) {
        edges{
            node {
                ... on AccountHolderCardProductApplicationOffer {
                    id
                    name
                    category
                    status
                }
            }
        }        
      }
      acceptedOffers: offers(filterBy: {offerStatus: {includes: [ACCEPTED]}}) {
        edges{
            node {
                ... on AccountHolderCardProductApplicationOffer {
                    id
                    name
                    category
                    status
                }
            }
        }        
      }
      accountHolderSnapshot {
        ... on USBusinessAccountHolderSnapshot {
          accountHolderCurrent {
            id
            primaryAuthorizedPerson {
              id
            }
          }
          businessProfile {
            currentVerification {
              status
              reason
              requiredDocuments {
                referenceIdentifier
                status
                uploadedDocuments {
                  __typename
                  ... on AccountHolderApplicationDocument {
                    status
                    createdAt
                    updatedAt
                    revisions {
                      status
                      type
                      createdAt
                      updatedAt
                    }
                  }
                }
                documentUploadSession {
                  __typename
                  ... on USAccountHolderApplicationDocumentUploadSession {
                    id
                    status
                    createdAt
                    updatedAt
                    primaryDocumentTypes
                    secondaryDocumentTypes
                    supportingDocumentTypes
                    uploadRequirements {
                      constraints {
                        __typename
                        ... on DocumentUploadRequirementConstraint {
                          documentType
                          numberOfDocuments
                        }
                      }
                    }
                    documents {
                      __typename
                      id
                      status
                      uploadUrl
                    }
                  }
                }
              }
            }
          }
          primaryAuthorizedPerson {
            name {
              givenName
              familyName
            }
