query GetTransferByID($id: ID!) {
  node(id: $id) {
    __typename
    ... on SecureCardBalanceRepaymentACHTransfer {
      createdAt
      updatedAt
      id
      amount {
        value
      }
      secureCardBalanceRepaymentAchTransferStatus: status {
        __typename
        ... on NonFailureIntegratorInitiatedACHStatus {
          status
        }
        ... on FailureIntegratorInitiatedACHStatus {
          status
          statusReasonCode
        }
      }
      settlementDate
    }
    ... on OneTimeACHTransfer {
      createdAt
      updatedAt
      id
      descriptor {
        companyEntryDescription
        individualName
        individualIdentificationNumber
      }
      transferAmount {
        ... on TransferAmountCalculation {
          __typename
          calculatedType
        }
        ... on ManualTransferAmount {
          amount {
            value
            currencyCode
          }
        }
      }
      status
      fromFinancialAccount {
        __typename
        ... on FinancialAccount {
          id
        }
      }
      toFinancialAccount {
        __typename
        ... on FinancialAccount {
          id
        }
      }
    }
    ... on SecureDepositACHTransfer {
      createdAt
      updatedAt
      id
      secureDepositAchTransferStatus: status {
        __typename
        ... on NonFailureIntegratorInitiatedACHStatus {
          status
        }
        ... on FailureIntegratorInitiatedACHStatus {
          status
          statusReasonCode
        }
      }
      amount {
        value
        currencyCode
      }
      fromFinancialAccount {
        __typename
        ... on FinancialAccount {
          id
          owner {
            __typename
          }
          application {
            id
          }
        }
      }
      toFinancialAccount {
        __typename
        ... on FinancialAccount {
          id
          owner {
            __typename
          }
          application {
            id
          }
        }
      }
      ledgers {
        id
        name
        ledgerEntries {
          edges {
            node {
              financialEvent {
                __typename
              }
            }
          }
        }
      }
    }
    ...on Transfer {
      id
      statusReason
      createdAt
      updatedAt
      amount {
        value
      }
      ledgers {
        id
        name
        normalBalance
        creditBalance {
          value
        }
        debitBalance {
          value
        }
        financialAccount {
          name
          owner {
            __typename
          }
        }
      }      
      createdAt
      updatedAt
    }
  }
}

query GetOneTimeACHTransferByID($id: ID!) {
  node(id: $id) {
    __typename
    ... on OneTimeACHTransfer {
      id
      fromFinancialAccount {
        ... on FinancialAccount {
          id
        }
      }
      toFinancialAccount {
        ... on FinancialAccount {
          id
        }
      }
      transferEvents(first: 10) {
        __typename
        pageInfo {
          endCursor
          startCursor
          hasNextPage
          hasPreviousPage
        }
        edges {
          __typename
          cursor
          node {
            ... on ScheduledTransferAchEvent {
              id
              paymentInitiatedAt
              fromFinancialAccount {
                ... on FinancialAccount {
                  id
                }
              }
              toFinancialAccount {
                ... on FinancialAccount {
                  id
                }
              }
              transferAmount {
                value
                currencyCode
              }
              transfers {
                ... on SecureCardBalanceRepaymentACHTransfer {
                  __typename
                  id
                  amount {
                    currencyCode
                    value
                  }
                  status {
                    ... on NonFailureIntegratorInitiatedACHStatus {
                      status
                    }
                    ... on FailureIntegratorInitiatedACHStatus {
                      status
                      statusReasonCode
                    }
                  }
                }
              }
            }
          }
        }
      }
      descriptor {
        companyEntryDescription
        individualName
        individualIdentificationNumber
      }
      transferAmount {
        ... on ManualTransferAmount {
          amount {
            value
            currencyCode
          }
        }
        ... on TransferAmountCalculation {
          calculatedType
        }
      }
      status
      scheduledTransferDate
      createdAt
      updatedAt
    }
  }
