mutation SimulateDeposit($input: SimulateDepositInput!) {
  simulateDeposit(input: $input) {
    ...on Transfer {
      id
      status
      createdAt
      updatedAt
      amount {
        value
      }
      ledgers {
        id
        name
        normalBalance
        financialAccount {
          id
        }
        debitBalance {
          value
        }
        creditBalance {
          value
        }
        ledgerEntries {
          edges {
            node {
              financialEvent {
                ... on Transfer {
                  id
                  amount {
                    value
                  }
                }
              }
            }
          }
        }
      }
    }
    ...on UserError {
      errors {
        code
        path
        description
      }
    }
  }
}

