query GetAllWebhookNotificationTargets($firstTargets: Int, $afterTargets: String) {
	organizations {
		id

		webhookNotificationTargets(first: $firstTargets, after: $afterTargets) {
			pageInfo {
				hasNextPage
				hasPreviousPage
				startCursor
				endCursor
			}

			edges {
				cursor
				node {
					__typename
					id
					name
					status
					deactivatedAt
					subscriptions

					statusHistory {
						__typename

						pageInfo {
							hasNextPage
							hasPreviousPage
							startCursor
							endCursor
						}

						edges {
							cursor

							node {
								__typename

								id
								createdAt
								newStatus
								previousStatus
							}
						}
					}
				}
			}
		}
	}
}

