query GetAllNotificationEvents($after: String, $first: Int) {
	notificationEvents(after: $after, first: $first) {
		edges {
			node {
				... on NotificationEvent {
					id
					createdAt
					name
					node {
                        __typename
						... on PaymentCardTransactionDispute {
							id
							status
							chargebacks {
                                id, 
                                status, 
                                dispute {
                                    id
                                }, 
                                creditStatus
                            }
						}
						... on PaymentCardTransactionChargeback {
							id
							creditStatus
							dispute {
                                id,
                                status,
                                amount { 
                                    value
							    }
                            }
							createdAt
							updatedAt
						}
					}
				}
			}
		}
	}
}

