query Reports($first: Int, $after: String, $input: ReportFilterInput) {
  reports(first: $first, after: $after, filterBy: $input) {
    ... on ReportConnection {
      edges {
        ... on ReportEdge {
          node {
            __typename
            id
            status
            reportType
            download {
              url
            }
            createdAt
            updatedAt
          }
        }
      }
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
    }
  }
}
