graphql.query:
mutation CreateUSPersonAccountHolderInManualReview(
  $input: CreateUSPersonAccountHolderInput!
) {
  createUSPersonAccountHolder(input: $input) {
    __typename
    ... on USPersonAccountHolder {
      id
      email
      dateOfBirth
      externalId
      updatedAt
      createdAt
      name {
        givenName
        familyName
        title
        suffix
        middleName
      }
      billingAddress {
        streetAddress
        extendedAddress
        postalCode
        region
        locality
        countryCodeAlpha3
      }
      phoneNumbers {
        countryCode
        number
        label
      }
      identificationDocument {
        socialSecurityNumber {
          numberHash
          countryCodeAlpha3
        }
      }
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
  }
}

mutation CreateUSPersonAccountHolderCardProductApplication($input: CreateAccountHolderCardProductApplicationInput!) {
  createAccountHolderCardProductApplication(input: $input) {
    __typename
      ... on AccountHolderCardProductApplication {
      id
      applicationState {
        status
      }
      cardProduct {
        id
      }  
      accountHolderSnapshot {
        ... on USPersonAccountHolderSnapshot {
          accountHolderCurrent {
            id
          }
          name {
            givenName
            familyName
          }
          billingAddress {
            streetAddress
            locality
            region
            countryCodeAlpha3
            postalCode
          }
          currentVerification {
            reason
            status
          }
        }
      }    
      updatedAt
      createdAt
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
  }
}

query GetDocumentUploadSessionsViaApplicationID(
  $id: ID!
  $firstApplicationHistory: Int
) {
  node(id: $id) {
    ... on AccountHolderCardProductApplication {
      __typename
      applicationState {
        status
      }
      cardProduct {
        name
      }
      accountHolderSnapshot {
        ...USPersonAccountHolderSnapshot
      }
    }
  }
}

fragment USPersonAccountHolderSnapshot on USPersonAccountHolderSnapshot {
  __typename
  currentVerification {
    status
    reason
    results {
      code
      description
    }
    requiredDocuments {
      createdAt
      referenceIdentifier
      updatedAt
      status
      documentUploadSession {
        ... on USAccountHolderApplicationDocumentUploadSession {
          status
          id
        }
      }
      uploadedDocuments {
        ... on AccountHolderApplicationDocument {
          status
          type
          createdAt
          updatedAt
        }
      }
    }
  }
}

mutation GenerateDocumentUploadClientToken($input: GenerateDocumentUploadClientTokenInput!) {
  generateDocumentUploadClientToken(input: $input) {
    ... on ClientToken {
      value
      expirationDate
    }
  }
}

mutation StartDocumentUploadSession($input: StartDocumentUploadSessionInput!) {
  startDocumentUploadSession(input: $input) {
    __typename
    ... on USAccountHolderApplicationDocumentUploadSession {
      id
      status
      primaryDocumentTypes
      secondaryDocumentTypes
      supportingDocumentTypes
      uploadRequirements {
        constraints {
          ... on DocumentUploadRequirementConstraint {
            documentType
            numberOfDocuments
          }
        }
      }
    }
    ... on UserError {
      errors {
        code
        description
      }
    }
  }
}

mutation CreateDocumentUploadSecureLink(
  $input: CreateDocumentUploadLinkInput!
) {
  createDocumentUploadLink(input: $input) {
    __typename
    ... on DocumentUploadLink {
      id
      documentUploadSession {
        id
        status
        ... on USAccountHolderApplicationDocumentUploadSession {
          uploadRequirements {
            constraints {
              ... on DocumentUploadRequirementConstraint {
                documentType
                numberOfDocuments
              }
            }
          }
        }
      }
      documentType
      status
      documentCategoryType
      uploadUrl
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
  }
}

mutation EndDocumentUploadSession($input: EndDocumentUploadSessionInput!) {
  endDocumentUploadSession(input: $input) {
    __typename
    ... on USAccountHolderApplicationDocumentUploadSession {
      id
      status
      primaryDocumentTypes
      secondaryDocumentTypes
      supportingDocumentTypes
      uploadRequirements {
        constraints {
          ... on DocumentUploadRequirementConstraint {
            documentType
            numberOfDocuments
          }
        }
      }
    }
  }
}

query GetDocumentUploadSession($id: ID!) {
  node(id: $id) {
    ... on DocumentUploadSession {
      id
      createdAt
      updatedAt
      status
      documents {
        ... on DocumentUploadLink {
          id
          status
          uploadUrl
          documentType
          documentCategoryType
        }
      }
    }
    ... on AccountHolderCardProductApplication {
      accountHolderSnapshot {
        ... on USPersonAccountHolderSnapshot {
          currentVerification {
            requiredDocuments {
              documentUploadSession {
                ... on USAccountHolderApplicationDocumentUploadSession {
                  id
                  status
                  primaryDocumentTypes
                  secondaryDocumentTypes
                  supportingDocumentTypes
                  uploadRequirements {
                    constraints {
                      ... on DocumentUploadRequirementConstraint {
                        documentType
                        numberOfDocuments
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}

mutation SimulateApplicationDocumentReview($input: SimulateApplicationDocumentReviewInput!) {
  simulateApplicationDocumentReview(input: $input) {
    ... on AccountHolderApplicationDocument {
      id
      status
    }
    ... on UserError {
      errors {
        code
        description
      }
    }
    ... on AccessDeniedError {
      message
    }
  }
}

mutation SimulateCreateApplicationDocumentsUploadSessions($input: SimulateCreateApplicationDocumentsUploadSessionsInput!) {
  simulateCreateApplicationDocumentsUploadSessions(input: $input) {
    ... on AccountHolderCardProductApplication {
      id
      applicationState {
        status
      }
    }
    ... on UserError {
      errors {
        description
        code
        errorPath
      }
    }
  }
}

mutation SimulateApplicationVerificationStatusChange($input: SimulateApplicationVerificationStatusChangeInput!) {
  simulateApplicationVerificationStatusChange(input: $input) {
    ... on AccountHolderCardProductApplication {
      id
      applicationState {
        status
      }
    }
    ... on UserError {
        errors {
            errorPath
            code
            description
        }
    }
  }
}

mutation SimulateApplicationStatusChange($input: SimulateApplicationStatusChangeInput!) {
  simulateApplicationStatusChange(input: $input) {
    ... on AccountHolderCardProductApplication {
      id
      applicationState {
        status
      }
    }
  }
}
