query GetAPIKeys($first: Int, $after: String) {
  organizations {
    apiKeys(first: $first, after: $after) {
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          name
          redactedKey
          createdAt
          revokedAt
        }
      }
    }
  }
}

mutation CreateSecretAPIKey($input: CreateSecretAPIKeyInput!) {
  createSecretAPIKey(input: $input) {
    __typename  
    ... on APIKey {
      id
      name
      key
    }
  }
}

mutation RevokeAPIKey($input: RevokeAPIKeyInput!) {
  revokeAPIKey(input: $input) {
    ...on APIKey {
      id
      revokedAt
    }
  }
}

query GetOrganizations {
  organizations {
    id
    profile {
      displayName
      environment
      region
    }
    financialAccounts {
      createdAt
      updatedAt
      __typename
      id
      name
      ledgers {
        creditBalance {
          value
        }
        debitBalance {
          value
        }
      }
    }
    cardProducts(first: 20) {
      pageInfo {
        startCursor
        endCursor
        hasNextPage
        hasPreviousPage
      }
      edges {
        cursor
        node {
          __typename
          id
          name
          usage
          vertical
          financialAccounts {
            id
            name
          }
        }
      }
    }
  }
}

mutation UpdateOrganizationProfileDisplayName($input: UpdateOrganizationProfileDisplayNameInput!) {
  updateOrganizationProfileDisplayName(input: $input) {
    ... on Organization {
      id
      profile {
        displayName
      }
    }
  }
}

query GetOrganizationByID($id: ID!) {
  node(id: $id) {
    ... on Organization {
      id
      profile {
        displayName
        environment
        region
      }
      financialAccounts {
        ledgers {
          id
          name
        }
      }
    }
  }
}

mutation CreateCardProduct($input: CreateCardProductInput!) {
  createCardProduct(input: $input) {
    __typename
    ... on CardProduct {
      id
      name
      usage
      vertical
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
  }
}

query GetAllCardProducts($first: Int) {
  cardProducts(first: $first) {
    pageInfo {
      startCursor
      endCursor
      hasNextPage
      hasPreviousPage
    }
    edges {
      cursor
      node {
        name
        __typename
        financialAccounts {
          id
          owner {
            __typename
          }
          name
          features {
            __typename
            enabled
          }
        }
        id
        usage
        vertical
      }
    }
  }
}

mutation UpdateCardProductName($input: UpdateCardProductNameInput!) {
  updateCardProductName(input: $input) {
    ... on CardProduct {
      id
      name
      usage
      vertical
    }
    ... on UserError {
      errors {
        code
        path
        description
      }
    }
  }
}

query GetCardProductByID($id: ID!) {
  node(id: $id) {
    ... on CardProduct {
      __typename
      id
      name
      usage
      features {
        __typename
        enabled
      }
      accounts {
        edges {
            node {
                id
                name
                owner {
                __typename
                }
                features {
                __typename
                createdAt
                updatedAt
                enabled
                }
            }
        }
      }
      transactionEvents(first: 10) {
        pageInfo {
          hasNextPage
        }
        edges {
          cursor
          node {
            __typename
            ... on Node {
              id
            }
            paymentCard {
              id
              last4
            }
            ... on AuthorizationEvent {
              requestedAmount {
                value
              }
              merchantDetails {
                name
              }
            }
          }
        }
      }
    }
  }
}

query GetAccountHolderPaymentCardsByCardProductID($cardProductId: ID!, $firstPaymentCards: Int, $paymentCardsFilterBy: CardProductPaymentCardsFilterInput) {
  node(id: $cardProductId) {
    ... on CardProduct {
      __typename
      id
      name
      usage
      paymentCards(first: $firstPaymentCards, filterBy: $paymentCardsFilterBy) {
        pageInfo {
          startCursor
          endCursor
          hasNextPage
          hasPreviousPage
        }
        edges {
          __typename
          cursor
          node {
            id
            bin
            application {
              accountHolderSnapshot {
                __typename
                ... on USPersonAccountHolderSnapshot {
                  name {
                    givenName
                    middleName
                    familyName
                  }
                }
              }
            }
            expirationDate
            last4
            status
          }
        }
      }
      cardProductApplications {
        pageInfo {
          hasNextPage
          hasPreviousPage
          startCursor
          endCursor
        }
        edges {
          node {
            id
            applicationState {
              status
            }
          }
        }
      }
    }
  }
}

mutation EnableCollaborativeApplicationUnderwritingFeature($input: EnableCollaborativeApplicationUnderwritingFeatureInput!) {
  enableCollaborativeApplicationUnderwritingFeature(input: $input) {
    ... on CardProduct {
      id
      name
      features {
        ... on CollaborativeApplicationUnderwritingCardProductFeature {
          enabled
          createdAt
          updatedAt
        }
      }
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
    ... on AccessDeniedError {
      message
    }
  }
}

query Ping {
  ping
}

query IntrospectionQuery {
  __schema {
    queryType {
      name
    }
    mutationType {
      name
    }
    subscriptionType {
      name
    }
    types {
      ...FullType
    }
    directives {
      name
      description

      locations
      args {
        ...InputValue
      }
    }
  }
}

fragment FullType on __Type {
  kind
  name
  description
  fields(includeDeprecated: true) {
    name
    description
    args {
      ...InputValue
    }
    type {
      ...TypeRef
    }
    isDeprecated
    deprecationReason
  }
  inputFields {
    ...InputValue
  }
  interfaces {
    ...TypeRef
  }
  enumValues(includeDeprecated: true) {
    name
    description
    isDeprecated
    deprecationReason
  }
  possibleTypes {
    ...TypeRef
  }
}

fragment InputValue on __InputValue {
  name
  description
  type {
    ...TypeRef
  }
  defaultValue
}

fragment TypeRef on __Type {
  kind
  name
  ofType {
    kind
    name
    ofType {
      kind
      name
      ofType {
        kind
        name
        ofType {
          kind
          name
          ofType {
            kind
            name
            ofType {
              kind
              name
              ofType {
                kind
                name
              }
            }
          }
        }
      }
    }
  }
}

