query GetWebhookNotificationTarget($id: ID!) {
	node(id: $id) {
		__typename
		... on WebhookNotificationTarget {
			status
			id

			deliveryAttempts {
				__typename

				pageInfo {
					hasNextPage
					hasPreviousPage
					startCursor
					endCursor
				}

				edges {
					cursor

					node {
						__typename
						id
						uri
						response {
							httpStatusCode
						}
						deliveryAttemptStatus
						createdAt
						event {
							name
							id
						}
					}
				}
			}

			statusHistory {
				__typename

				pageInfo {
					hasNextPage
					hasPreviousPage
					startCursor
					endCursor
				}

				edges {
					cursor

					node {
						__typename

						id
						createdAt
						newStatus
						previousStatus
					}
				}
			}
		}
	}
}

query GetNotificationTargetDeliveryAttempts($id: ID!, $first: Int, $after: String) {
  node(id: $id) {
    __typename
    ... on WebhookNotificationTarget {
      id
      deliveryAttempts(first: $first, after: $after) {
        __typename
        pageInfo {
          hasNextPage
          hasPreviousPage
          startCursor
          endCursor
        }
        edges {
          cursor
          node {
            __typename
            id
            uri
            response {
				httpStatusCode
			}
            deliveryAttemptStatus
            createdAt
            event {
              name
              id
            }
          }
        }
      }
    }
  }
}

