      cardProduct {
        id
      }
      ledgers {
        id
        name
        normalBalance
        creditBalance {
          value
        }
        debitBalance {
          value
        }
        ledgerEntries {
          edges {
            node {
              __typename
              id
              createdAt
              amount {
                value
                currencyCode
              }
              financialEvent {
                ... on Transfer {
                  amount {
                    value
