query GetAccountHolderCardProductApplicationStatus(
  $id: ID!
  $firstApplicationHistory: Int
) {
  node(id: $id) {
    ... on AccountHolderCardProductApplication {
      __typename
      id
      createdAt
      updatedAt
      applicationState {
        status
      }
      cardProduct {
        name
      }
      accountHolderSnapshot {
        ...USPersonAccountHolderSnapshot
      }
      applicationHistory(first: $firstApplicationHistory) {
        pageInfo {
          hasNextPage
          hasPreviousPage
          startCursor
          endCursor
        }
        edges {
          node {
            ...ApplicationHistorySnapshot
          }
        }
      }
    }
  }
}

fragment Verification on AccountHolderVerification {
  status
  reason
  results {
      code
      description
    }
  requiredDocuments {
    __typename
    createdAt
    referenceIdentifier
    updatedAt
    status
    documentUploadSession {
      ... on USAccountHolderApplicationDocumentUploadSession {
        status
        id
        primaryDocumentTypes
        secondaryDocumentTypes
        uploadRequirements {
          constraints {
            ... on DocumentUploadRequirementConstraint {
              numberOfDocuments
              documentType
            }
          }
        }
      }
    }
    uploadedDocuments {
      ... on AccountHolderApplicationDocument {
        status
        type
        createdAt
        updatedAt
      }
    }
  }
}

fragment USPersonAccountHolderSnapshot on USPersonAccountHolderSnapshot {
  __typename
  accountHolderCurrent {
    id
  }
  name {
    givenName
    familyName
    title
    suffix
  }
  currentVerification {
    ...Verification
  }
}


fragment ApplicationHistorySnapshot on AccountHolderCardProductApplicationSnapshot {
  __typename
  createdAt
  updatedAt
  applicationState {
    status
  }
}

query GetDocumentUpload_via_SessionID($id: ID!) {
	node(id: $id) {
		__typename
		... on USAccountHolderApplicationDocumentUploadSession {
			__typename
			id
			status
			primaryDocumentTypes
			secondaryDocumentTypes
			supportingDocumentTypes
			uploadRequirements {
				constraints {
					... on DocumentUploadRequirementConstraint {
						documentType
						numberOfDocuments
					}
				}
			}
			documents {
				... on DocumentUploadLink {
					id
					status
					documentCategoryType
					documentType
					status
					documentUploadSession {
						id
						status
					}
				}
			}
		}
	}
}

mutation GenerateDocumentUploadClientToken($input: GenerateDocumentUploadClientTokenInput!) {
  generateDocumentUploadClientToken(input: $input) {
    ... on ClientToken {
      value
      expirationDate
    }
  }
}

mutation StartDocumentUploadSession($input: StartDocumentUploadSessionInput!) {
  startDocumentUploadSession(input: $input) {
    __typename
    ... on USAccountHolderApplicationDocumentUploadSession {
      id
      status
      primaryDocumentTypes
      secondaryDocumentTypes
      supportingDocumentTypes
      uploadRequirements {
        constraints {
          ... on DocumentUploadRequirementConstraint {
            documentType
            numberOfDocuments
          }
        }
      }
    }
    ... on UserError {
      errors {
        code
        description
      }
    }
  }
}

mutation CreateDocumentUploadSecureLink(
  $input: CreateDocumentUploadLinkInput!
) {
  createDocumentUploadLink(input: $input) {
    __typename
    ... on DocumentUploadLink {
      id
      documentUploadSession {
        id
        status
        ... on USAccountHolderApplicationDocumentUploadSession {
          uploadRequirements {
            constraints {
              ... on DocumentUploadRequirementConstraint {
                documentType
                numberOfDocuments
              }
            }
          }
        }
      }
      documentType
      status
      documentCategoryType
      uploadUrl
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
  }
}

mutation EndDocumentUploadSession($input: EndDocumentUploadSessionInput!) {
  endDocumentUploadSession(input: $input) {
    __typename
    ... on USAccountHolderApplicationDocumentUploadSession {
      id
      status
      primaryDocumentTypes
      secondaryDocumentTypes
      supportingDocumentTypes
      uploadRequirements {
        constraints {
          ... on DocumentUploadRequirementConstraint {
            documentType
            numberOfDocuments
          }
        }
      }
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
    ... on AccessDeniedError {
      message
    }
  }
}
