        }
      }

      restrictedDetails {
        ... on PaymentCardRestrictedDetails {
          cvv
          number
        }
        ... on AccessDeniedError {
          message
        }
      }
    }
    ... on UserError {
      errors {
        path
        code
        description
      }
    }
  }
}

